function openWebsite(url) {
	url = url.split(" ");
	url = url.join("+");
	url = encodeURI( url );
	if (File.fs == "Macintosh") {
		var tempFile = File("~/Desktop/tempurl.webloc");
		tempFile.open("w");
		tempFile.write("<?xml version=\"1.0\" encoding=\"UTF-8\"?>\
		<!DOCTYPE plist PUBLIC \"-//Apple//DTD PLIST 1.0//EN\" \"http://www.apple.com/DTDs/PropertyList-1.0.dtd\">\
		<plist version=\"1.0\">\
			<dict>\
				<key>URL</key>\
				<string>"+url+"</string>\
			</dict>\
		</plist>");
		tempFile.close();
		tempFile.execute();
		for (var aux = 0; aux < 100; aux++) {
			$.sleep(10);
		}
		tempFile.remove();
	} else {
		var tempFile = File("~/Desktop/cuppascript.url");
		tempFile.open("w");
		tempFile.write("[InternetShortcut]\
URL="+url);
		tempFile.close();
		tempFile.execute();
		for (var aux = 0; aux < 100; aux++) {
			$.sleep(10);
		}
		tempFile.remove();
	}
}

function open_folders() {
  var ud_folder = Folder.userData;
  var cs_folders = ud_folder.getFiles( function(f) {
    var _n = "cs_octopus"
    return (f.constructor.name == "Folder") && (f.name.substr(0, _n.length ) == _n);
  })
  var sfs = get_paths_to_script_folder();

  for ( var n = 0; n < cs_folders.length; n++ ) {
    cs_folders[n].execute();
  }
  sfs.panel.execute();
  sfs.startup.execute();
}
