var script_version = "1.0.5";

init();
init_classes();
install_files();


function install_files() {

  if ( ! zustimmung() ) return;

  // -------------------------------------------------------------------------------------
  //  Remote JSON laden
  // -------------------------------------------------------------------------------------
  try {
    var _json_path = download("data", gd.json_url, "", gd.config_name, undefined, true);
  } catch(e) {
    var os = $.os;
    // Result: Macintosh OS 10.14.6
    if ( os.indexOf("Macintosh") != -1 && os.indexOf("10.14.") != -1 ) {
      alert( __("curl-problem") );
    } else {
      alert( __("Download-Fehler") + "\n" + e);
    }
    return;
  }
  if ( ! _json_path ) {
    alert(__("not-online"));
    return;
  }
  var _remote_data = read_file(_json_path);
  var remote_satz = new Satz(_remote_data);

  // -------------------------------------------------------------------------------------
  //  Installer-Startup installieren
  // -------------------------------------------------------------------------------------
  for (var sid in remote_satz.scripts) {
    if ( sid == "octopus_installer" || sid == "Octopus-Installer" || sid == "Octopus-Includes") {
      var _s = remote_satz.get_script(sid);
      try {
        _s.download();
      } catch(e) {
        // log ( "Fehler beim Laden des Scripts", sid );
      }
      _s.install();
      // var sp = get_script_path();
      // if ( sp.dbg == false ) {
        // Nicht in VSCode
        // var _manuelles_script = new File( sp.path );
        // if ( ! dev ) _manuelles_script.remove();
        // log ("Manuell installierter Installer wurde entfernt");
      // }
    }
  }
  cs_alert("", __('meldung'),
    undefined, 
    "OK"
  )
}
function __( id ) {
  if ( typeof loc_strings == "undefined" ) loc_strings = load_translation();
  if (loc_strings.hasOwnProperty(id)) {
    return localize(loc_strings[id]);
  } else {
    return id
  }
}
