function fread(f) {
  f.encoding = "UTF-8";
  f.open("r");
  var aux = f.read();
  f.close();
  return aux;
}
function fwrite(f, ctnt, append) {
  f.encoding = "UTF-8";
  if ( append ) {
    f.open("a")
  } else {
    f.open("w");
  }
  var aux = f.write( ctnt );
  f.close();
  return aux;
}

